import React, { useCallback, useEffect, useRef, useState } from "react";
import { GoogleMap, MarkerF, useJsApiLoader } from "@react-google-maps/api";

import data from "./mapData/TripScheduleData";

/*
const center = {
	lat: 37.7189,
	lng: 128.8321,
}

export default function Map() {

	const mapRef = useRef(null)

	const initMap = useCallback(() => {
			new window.google.maps.Map(mapRef.current, {
				center: { lat: 37.7189, lng: 128.8321 },
				zoom: 12,
				disableDefaultUI: true,
			})
		}
	, [mapRef])

	useEffect(() => {
		initMap()
	}, [initMap])

	return <div className='map' style={{ width: '100%', height: '100%' }} ref={mapRef}></div>
}
*/

const containerStyle = {
  width: "100%",
  height: "100%",
};

// 일정 order:1에서 시작
// const center = data.find((item) => item.order === 1)?.center;

/* ---------- planList1 데이터 구조 ----------

1: [
      //DAY1
      { order: 1, placeOrder: 1, isChecked: false, type: 'place', title: '경포 해변1', subtitle: '관광명소 · 강릉' },
      { order: 2, placeOrder: 2, isChecked: false, type: 'place', title: '경포 해변2', subtitle: '관광명소 · 강릉' },
  ],
  2: [
      //DAY2
      { order: 1, placeOrder: 1, isChecked: false, type: 'place', title: '문릿', subtitle: '음식점 · 양평' },
      { order: 2, placeOrder: 2, isChecked: false, type: 'place', title: '양평 두물머리', subtitle: '관광지 · 양평' },
    ],

    title: '문릿
    = planList[2][0].title
    첫번째 배열은 1부터 시작, 두번째 배열은 0부터 시작

*/

export default function Map({ planList, selectedCity, selectedDay }) {
  // 페이지 첫 로드 시, selectedCity Lat, Lng에서 시작(key_name: lat, lng로 사용해야 함)
  const [center, setCenter] = useState({ lat: selectedCity.mapLat, lng: selectedCity.mapLon });

  useEffect(() => {
    const selectedPlan = Object.values(planList).find((dayList) =>
      dayList.some((item) => item.order === selectedDay)
    );

    if (selectedPlan) {
      const selectedItem = selectedPlan.find((item) => item.order === selectedDay);
      setCenter({ lat: selectedItem.lat, lng: selectedItem.lng });
    }

    console.log("center: ", center);

  }, [selectedDay]);

  useEffect(() => {
    console.log("저기요: ", selectedDay);
  }, [selectedDay]);

  const { isLoaded } = useJsApiLoader({
    id: "google-map-script",
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAP_API_KEY, // googleMap API personal key - 환경변수로 넘기기
  });

  const [map, setMap] = React.useState(null);

  const onLoad = React.useCallback(
    function callback(map) {
      // Create boundary
      const bounds = new window.google.maps.LatLngBounds(center);
      // 시작시 zoom level 조정
      bounds.extend(
        new window.google.maps.LatLng(center.lat + 0.05, center.lng + 0.05)
      );
      bounds.extend(
        new window.google.maps.LatLng(center.lat - 0.05, center.lng - 0.05)
      );
      map.fitBounds(bounds);
      setMap(map);

      // Polyline 지도 추가
      polyline.setMap(map);
      // GoogleMap 컴포넌트가 로드되고 map 객체가 생성된 후에 호출되어야 함
    },
    [selectedDay] // SelectedDay 값이 변경될 때마다 새로운 onLoad 콜백 함수가 생성
  );

  // 구글 MAP 기본 Marker(박물관, 음식점 등) 삭제
  const myStyles = [
    {
      featureType: "poi",
      elementType: "labels",
      stylers: [{ visibility: "ON" }],
    },
  ];

  const onUnmount = useCallback(function callback(map) {
    setMap(null);
  }, []);

  // 점선 설정
  const coordinates = planList[selectedDay]
    .filter((item) => item.type !== "memo")
    .map((item) => ({ lat: item.lat, lng: item.lng }));

  const lineSymbol = {
    path: "M 0,-1 0,1",
    // M: 선분 시작
    // (0, -1): 선분 시작 좌표
    // (0, 1): 선분 끝 좌표
    strokeOpacity: 0.7, // 투명도
    scale: 3, // 두께
  };

  const polyline = new window.google.maps.Polyline({
    path: coordinates,
    strokeColor: "#555555",
    strokeOpacity: 0,
    strokeWeight: 0.7,

    icons: [
      {
        icon: lineSymbol,
        offset: "0",
        repeat: "15px", // 실선과 실선 사이 길이
      },
    ],
  });

  return isLoaded ? (
    <GoogleMap
      key={selectedDay} // selectedDay 변경에 따른 구글맵 reLoad
      mapContainerStyle={containerStyle}
      center={center}
      zoom={12}
      onLoad={onLoad}
      onUnmount={onUnmount}
      options={{
        disableDefaultUI: true, // 구글 Map 기본 옵션 삭제
        styles: myStyles,
        // gestureHandling: "cooperative", // ctrl 확대, 축소 안내 문구 삭제 ▶ 작동 X
      }}
    >
      {/* Child components, such as markers, info windows, etc. */}
      <>
        {planList[selectedDay]
          .filter((item) => item.placeOrder !== null)
          .map((item) => (
            <MarkerF
              key={item.placeOrder} // index가 0부터 시작하므로 placeOrder 활용
              position={{ lat: item.lat, lng: item.lng }} // Marker Location
              icon={{
                url: `${process.env.PUBLIC_URL}/images/mapMarker/marker_num_${item.placeOrder}.png`,
                scaledSize: new window.google.maps.Size(30, 30), // 이미지 사이즈 조정
                anchor: new window.google.maps.Point(15, 15), // 마커 이미지 위치 조정에 따른 앵커 포인트 지정
              }}
            />
          ))}
        {planList[selectedDay]
          .filter((item) => item.type === "dorm")
          .map((item) => (
            <MarkerF
              key={item.order}
              position={{ lat: item.lat, lng: item.lng }}
              icon={{
                url: `${process.env.PUBLIC_URL}/images/mapMarker/marker_dorm.png`,
                scaledSize: new window.google.maps.Size(30, 30), // 이미지 사이즈 조정
                anchor: new window.google.maps.Point(15, 15), // 마커 이미지 위치 조정에 따른 앵커 포인트 지정
              }}
            />
          ))}
      </>
    </GoogleMap>
  ) : (
    <></>
  );
}
